   document.addEventListener(\"DOMContentLoaded\", function() {\n        window.onload = function startup() {\n            // 1. call API to fetch challenge\n            fetch('http://stormbird.duckdns.org:8080/api/getChallenge')\n                .then(function (response) {\n                    return response.text()\n                })\n                .then(function (response) {\n                    document.getElementById('msg').innerHTML = 'Challenge: ' + response\n                    window.challenge = response\n                })\n        }\n\n        window.onConfirm = function onConfirm(signature) {\n            if (window.challenge === undefined || window.challenge.length == 0) return\n            const challenge = window.challenge\n            document.getElementById('status').innerHTML = 'Wait for signature...'\n            // 2. sign challenge to generate response\n            web3.personal.sign({ data: challenge }, function (error, value) {\n                if (error != null) {\n                    document.getElementById('status').innerHTML = error\n                    window.onload();\n                    return\n                }\n\n                document.getElementById('status').innerHTML = 'Verifying credentials ...'\n                // 3. open door\n                let contractAddress = document.getElementById(\"contractAddress\").textContent;\n                fetch(`http://stormbird.duckdns.org:8080/api/checkSignature?contract=${contractAddress}&amp;challenge=${challenge}&amp;sig=${value}`)\n                    .then(function (response) {\n                        return response.text()\n                    })\n                    .then(function (response) {\n                        if (response == \"pass\") {\n                            document.getElementById('status').innerHTML = 'Entrance granted!'\n                            window.close()\n                        } else {\n                            document.getElementById('status').innerHTML = 'Failed with: ' + response\n                        }\n                    })\n            });\n            window.challenge = '';\n            document.getElementById('msg').innerHTML = '';\n        }\n    })\n</script>\n<h3>Welcome to Craig Wright's house!</h3>\n<div id=\"msg\">Preparing to unlock the entrance door.</div>\n<div id=\"contractAddress\"/>\n<div id=\"status\"/>\n\n      </ts:view>\n    </ts:action>\n  </ts:cards>\n  <ts:attribute-types>\n    <ts:attribute-type id=\"locality\" syntax=\"1.3.6.1.4.1.1466.115.121.1.15\">\n      <ts:origins>\n        <ts:ethereum as=\"utf8\" contract=\"EntryToken\" function=\"getLocality\">\n            <ts:data>\n              <ts:uint256 ref=\"tokenId\"/>\n            </ts:data>\n        </ts:ethereum>\n      </ts:origins>\n\n    </ts:attribute-type>\n    <ts:attribute-type id=\"time\" syntax=\"1.3.6.1.4.1.1466.115.121.1.24\">\n      <ts:name>\n        <ts:string xml:lang=\"en\">Time</ts:string>\n        <ts:string xml:lang=\"zh\">时间</ts:string>\n      </ts:name>\n      <ts:origins>\n        <ts:token-id as=\"utf8\" bitmask=\"0000FFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF0000000000000000000000\"/>\n      </ts:origins>\n    </ts:attribute-type>\n    <ts:attribute-type id=\"expired\" syntax=\"1.3.6.1.4.1.1466.115.121.1.7\"> <!-- boolean -->\n      <ts:origins>\n        <ts:ethereum as=\"bool\" contract=\"EntryToken\" function=\"isExpired\">\n          <ts:data>\n            <ts:uint256 ref=\"tokenId\"/>\n          </ts:data>\n        </ts:ethereum>\n      </ts:origins>\n    </ts:attribute-type>\n    <ts:attribute-type id=\"street\" syntax=\"1.3.6.1.4.1.1466.115.121.1.15\"> <!-- string -->\n      <ts:origins>\n        <ts:ethereum as=\"utf8\" contract=\"EntryToken\" function=\"getStreet\">\n          <ts:data>\n            <ts:uint256 ref=\"tokenId\"/>\n          </ts:data>\n        </ts:ethereum>\n      </ts:origins>\n    </ts:attribute-type>\n    <ts:attribute-type id=\"building\" syntax=\"1.3.6.1.4.1.1466.115.121.1.15\"> <!-- string -->\n      <ts:origins>\n        <ts:ethereum as=\"utf8\" contract=\"EntryToken\" function=\"getBuildingName\">\n          <ts:data>\n            <ts:uint256 ref=\"tokenId\"/>\n          </ts:data>\n        </ts:ethereum>\n      </ts:origins>\n    </ts:attribute-type>\n    <ts:attribute-type id=\"state\" syntax=\"1.3.6.1.4.1.1466.115.121.1.15\"> <!-- string -->\n      <ts:origins>\n        <ts:ethereum as=\"utf8\" contract=\"EntryToken\" function=\"getState\">\n          <ts:data>\n            <ts:uint256 ref=\"tokenId\"/>\n          </ts:data>\n        </ts:ethereum>\n      </ts:origins>\n    </ts:attribute-type>\n</ts:attribute-types>\n<ds:Signature xmlns:ds=\"http://www.w3.org/2000/09/xmldsig#\">\n<ds:SignedInfo>\n<ds:CanonicalizationMethod Algorithm=\"http://www.w3.org/2001/10/xml-exc-c14n#\"/>\n<ds:SignatureMethod Algorithm=\"http://www.w3.org/2001/04/xmldsig-more#rsa-sha256\"/>\n<ds:Reference URI=\"\">\n<ds:Transforms>\n<ds:Transform Algorithm=\"http://www.w3.org/2000/09/xmldsig#enveloped-signature\"/>\n<ds:Transform Algorithm=\"http://www.w3.org/2001/10/xml-exc-c14n#\"/>\n</ds:Transforms>\n<ds:DigestMethod Algorithm=\"http://www.w3.org/2001/04/xmlenc#sha256\"/>\n<ds:DigestValue>z+I6NxdALVtlc3TuUo2QEeV9rwyAmKB4UtQWkTLQhpE=</ds:DigestValue>\n</ds:Reference>\n</ds:SignedInfo>\n<ds:SignatureValue>\nE9H57w4vE8dP/7gZYMLm4z+AohkuIWgvqiGfCnMmy87T25Q1utzlFHE71isNvdkBooBHKnFaLm4X\n7HRSdjNyNt8UIqp8ekJZt4zK/GyUZ9Fk290A4Bey6YEko2atQSCq1wRKRbbhbyT1rggYdSVfGc85\nbQ2fnLzLETITeo5Cbp6ShcWUhdPqCSSFOoIKoIpIJG/lXNa/IFUWtBs07K6S5G77O/mPXkugzpjw\nASDyefe7Z2FPgHyebrOkyDHaS3ycnqJB11r0I4ve5j9cVsjarxfRzyGzbZri/qvbr7ov3J0HDXyl\nIQ/01ct32V6UIXlqPgsk12IcqtY16BwQalIx0A==\n</ds:SignatureValue>\n<ds:KeyInfo>\n<ds:KeyName>Shong Wang</ds:KeyName>\n<ds:KeyValue>\n<ds:RSAKeyValue>\n<ds:Modulus>\nql1jleB5eU+FiIK50QwMc1pS6ieMxqciwdYHdOalEyLZZ0Swt+3Ls24g1Ktar+aJJjSJO8OjUNse\nBqECPSP2rGYIfNN7echgXEJtr9MZ5vz/G+zEyZB4lfJaBb3iGreQerdgktcjla6PC/bSiFrWs9zA\ntMp1uowMWV2T5LY7N1SYw6uLP3c8C